class postResponse {
    constructor() {
        this.actions = {
            Redirect: 'redirect',
            Load: 'load',
            Dialog: 'dialog',
            Nothing: 'nothing'
        };
        this.dataTypes = {
            HTML: 'html',
            URL: 'url'
        };
        this.msgTypes = {
            incorrectUserOrPassword: 'Nombre de usuario o contrase침a incorrectos.',
            sessionNotFound: 'Ocurri칩 un error al validar tu sesi칩n.',
            databaseError: 'Ocurri칩 un error al consultar la base de datos.'
        };
    
        this.action = 'nothing';
        this.data = {
            type: 'html',
            html: null,
            url: null,
            data: null
        };
        this.postResponse();
    }
    postResponse() {

    }

    categorize(action, dataType) {
        this.action = action;
        this.data.type = dataType;
    }
    setMessage(msgType) {
        this.categorize(this.actions.Dialog, this.dataTypes.HTML);
        this.data.html = '<p>' + msgType + '</p>';
    }
    stringify() {
        var ret = JSON.stringify(
            {
                action: this.action,
                data: this.data
            }
        );
        return ret;
    }
};
module.exports = postResponse;