/**
 * Server POST requests responser class.
 *
 * @link   https://github.com/DianaLeNeant/ArtemisaNode
 * @author Diana Celeste Nuño Ramírez. 2018.
 */
class postResponse {
    constructor() {
        this.actions = {
            Redirect: 'redirect',
            Load: 'load',
            Dialog: 'dialog',
            Nothing: 'nothing'
        };
        this.dataTypes = {
            HTML: 'html',
            URL: 'url'
        };
        this.msgTypes = {
            incorrectUserOrPassword: 'Nombre de usuario o contraseña incorrectos.',
            sessionNotFound: 'Ocurrió un error al validar tu sesión.',
            databaseError: 'Ocurrió un error al consultar la base de datos.'
        };
    
        this.action = 'nothing';
        this.data = {
            type: 'html',
            html: null,
            url: null,
            data: null
        };
        this.postResponse();
    }
    postResponse() {

    }

    categorize(action, dataType) {
        this.action = action;
        this.data.type = dataType;
    }
    setMessage(msgType) {
        this.categorize(this.actions.Dialog, this.dataTypes.HTML);
        this.data.html = '<p>' + msgType + '</p>';
    }
    stringify() {
        var ret = JSON.stringify(
            {
                action: this.action,
                data: this.data
            }
        );
        return ret;
    }
};
module.exports = postResponse;
